create database `supermarche`;

use `supermarche`;

CREATE TABLE `client` (
  `id` mediumint(8) NOT NULL auto_increment,
  `nom_client` varchar(255) default NULL,
  `prenom_client` varchar(255) default NULL,
  `age_client` mediumint default NULL,
  `adresse_client` varchar(255) default NULL,
  PRIMARY KEY (`id`)
) AUTO_INCREMENT=1;

CREATE TABLE `produit` (
  `numero` mediumint(8) auto_increment,
  `libelle` varchar(255) default NULL,
  PRIMARY KEY (`numero`)
) AUTO_INCREMENT=1;

CREATE TABLE `commande` (
  `id` mediumint(8) unsigned NOT NULL auto_increment,
  `reference` varchar(13) default NULL,
  `date` varchar(255),
  `id_client` mediumint(8),
  `numero_produit` mediumint(8),
  PRIMARY KEY (`id`),
  FOREIGN KEY (id_client) REFERENCES CLIENT(id),
  FOREIGN KEY (numero_produit) REFERENCES PRODUIT(numero)
) AUTO_INCREMENT=1;

